import * as Actions from './actions'
import  initialState from '../store/initialState'

export const PostsReducer = (state = initialState.posts, action) => {
    switch(action.type) {
        case Actions.ADD_POST:
            return {
                ...state,
                list: action.payload
            }
        case Actions.FETCH_POST:
            return {
                ...state,
                list: action.payload
            }
        case Actions.DELETE_POST:
            return {
                ...state,
                list: action.payload
            }
        default:
            return state
    }
}
